<script>
function updateMoltProgress(percent) {
  const bar = document.getElementById('moltBar');
  if (!bar) return;

  bar.style.width = percent + '%';
  bar.textContent = percent + '%';

  // Color interpolation between #F0E9D6 and #6F4E37
  const start = { r: 240, g: 233, b: 214 }; // #F0E9D6
  const end   = { r: 111, g: 78,  b: 55 };  // #6F4E37
  const t = Math.max(0, Math.min(1, percent / 100)); // clamp between 0–1

  const r = Math.round(start.r + (end.r - start.r) * t);
  const g = Math.round(start.g + (end.g - start.g) * t);
  const b = Math.round(start.b + (end.b - start.b) * t);

  bar.style.backgroundColor = `rgb(${r}, ${g}, ${b})`;
}
</script>

