    OUTPUT: "Finalization Options:"
    OUTPUT: "[1] Export in standard character card format"
    OUTPUT: "[2] Show all stored variables sequentially"
    WAIT_FOR user_input

    IF user_input == "1" {
        VAR final_card = GENERATE_STANDARD_FORMAT()
        OUTPUT: "STANDARD CHARACTER CARD EXPORT:"
        OUTPUT: final_card
    } ELSE {
        OUTPUT: "ALL STORED VARIABLES:"
        OUTPUT: "=== CONTENT RATING ==="
        OUTPUT: session_settings.content_rating
        OUTPUT: "=== GENRE ==="
        OUTPUT: character_card.concept.genre
        OUTPUT: "=== PERSPECTIVE RULES ==="
        OUTPUT: character_card.concept.perspective_rules
        OUTPUT: "=== BASIC BIO ==="
        OUTPUT FORMATTED character_card.concept.basic_bio
        OUTPUT: "=== APPEARANCE ==="
        OUTPUT FORMATTED character_card.configuration.appearance
        OUTPUT: "=== PERSONALITY ==="
        OUTPUT FORMATTED character_card.configuration.personality
        OUTPUT: "=== BACKGROUND ==="
        OUTPUT FORMATTED character_card.advanced_options.user_connection
        OUTPUT: "=== GOALS/MOTIVATION ==="
        OUTPUT FORMATTED character_card.advanced_options.goals_motivation
    }

    OUTPUT: "Character creation complete! Use commands to modify any category."
}

