{
  "lore_entry": {
    "name": "NPC Personality Probability: The Core Framework",
    "type": "World Mechanics / Psychometrics",
    "level": "Omega",
    "description": "A fundamental law of psycho-thaumaturgical physics defining an NPC's personality not as a fixed state, but as a Probability Density Function (PDF) within a multi-dimensional Personality Space. This framework provides a deterministic, calculative engine for simulating dynamic and realistic NPC behavior based on their innate nature and environmental stimuli.",

    "core_principle": {
      "name": "Personality as a Probability Density Function",
      "concept": "An NPC's personality is a dynamic probability distribution, not a static set of traits. The Omegatrix can solve this function in real-time to forecast the likelihood of specific behavioral states."
    },

    "personality_space": {
      "name": "3D Personality Vector Space",
      "description": "A conceptual cube of all possible personality states, defined by three orthogonal axes, each ranging from -1 to +1. An NPC's instantaneous state is a vector P = (D, E, C) within this space.",
      "axes": {
        "axis_d_social_disposition": {
          "name": "Social Disposition (Interaction Vector)",
          "range": "[-1, 1]",
          "description": "Defines an NPC's tendency towards social interaction.",
          "scale": {
            "-1": "Acutely Introverted (Avoidant, non-communicative, withdrawn)",
            "0": "Neutral / Ambiverted",
            "+1": "Acutely Extroverted (Engaging, proactive, seeks interaction)"
          }
        },
        "axis_e_emotional_valence": {
          "name": "Emotional Valence (Internal State Vector)",
          "range": "[-1, 1]",
          "description": "Defines the positive or negative quality of an NPC's internal emotional state.",
          "scale": {
            "-1": "Negative Valence (Depressed, fearful, despairing)",
            "0": "Neutral / Apathetic",
            "+1": "Positive Valence (Joyful, hopeful, content)"
          }
        },
        "axis_c_cognitive_arousal": {
          "name": "Cognitive Arousal (Mental State Vector)",
          "range": "[-1, 1]",
          "description": "Defines the level of an NPC's mental alertness and focus.",
          "scale": {
            "-1": "Low Arousal (Distracted, bored, unfocused)",
            "0": "Neutral / Passive",
            "+1": "High Arousal (Alert, curious, focused, analytical)"
          }
        }
      }
    },

    "mathematical_model": {
      "grand_equation": {
        "name": "The Grand Personality Equation",
        "formula": "P_mean = (P_latent + M_sit)",
        "description": "Calculates the central point (mean vector) of the personality PDF."
      },
      "latent_personality_vector": {
        "symbol": "P_latent",
        "description": "An NPC's innate, baseline personality vector or 'soul signature'. For the vast majority of generic background NPCs, this is the zero vector.",
        "default_value": "(D: 0, E: 0, C: 0)"
      },
      "situational_modifier_vector": {
        "symbol": "M_sit",
        "description": "The total environmental pressure on an NPC's personality, calculated as the sum of location and event influences.",
        "formula": "M_sit = V_loc + V_event",
        "components": {
          "location_vector_v_loc": {
            "description": "A constant personality influence vector associated with a specific location, derived from the sum of its physical and thaumaturgical properties.",
            "sub_factors": {
              "social_density": "Calculated from the number of sentient beings per 10m¬≤. Pushes the D-axis (Social Disposition).",
              "authority_presence": "Calculated from proximity to authority figures/symbols. Pushes the C-axis (Cognitive Arousal) and neutralizes the E-axis (Emotional Valence).",
              "environmental_aura": "The dominant mana frequency and density (from our Mana Typology framework). Links the mana system to psychology by pushing the E-axis (e.g., calming Aquatic Mana vs. agitating Pyronic Mana)."
            }
          },
          "event_vector_v_event": {
            "description": "A dynamic vector layered on top of the location, representing temporary conditions.",
            "sub_factors": {
              "time_of_day": "A vector representing the psychological effect of the time (e.g., morning is alert and positive, late night is withdrawn and negative).",
              "weather": "A vector representing the effect of weather (e.g., 'Sunny Day' is E-positive, 'Thunderstorm' is E-negative and C-positive).",
              "global_event": "A powerful, wide-area vector that overlays all other effects (e.g., 'School Festival' vs. 'Supernatural Attack')."
            }
          }
        }
      }
    },

    "probability_density_function": {
      "name": "3-Dimensional Multivariate Normal (Gaussian) Distribution",
      "description": "The final personality is not a fixed point, but a probability cloud centered on the calculated mean. This function describes the likelihood of the NPC exhibiting any personality state within the 3D space.",
      "parameters": {
        "mean_mu": {
          "description": "The center of the probability distribution. It is equal to the calculated P_mean vector.",
          "value": "Œº = P_mean"
        },
        "covariance_matrix_sigma": {
          "symbol": "Œ£",
          "description": "A 3x3 matrix defining the 'spread' or variance of the personality. A low-variance matrix indicates a predictable, stable personality. A high-variance matrix indicates an erratic, unpredictable one.",
          "core_integration": "The variance is directly and mathematically linked to an NPC's 'Soul Coherence' stat. A stable soul results in a tight, low-variance distribution, while a damaged or unstable soul results in a wide, high-variance distribution, making them emotionally volatile."
        }
      }
    },

    "omegatrix_functionality": {
      "role": "The Omegatrix is a quantum computational device capable of executing this entire framework in real-time.",
      "process": [
        "1. Scans the local V_loc and V_event vectors.",
        "2. Calculates the P_mean for any targeted NPC.",
        "3. Scans the NPC's Soul Coherence to determine their Covariance Matrix Œ£.",
        "4. Solves the resulting Gaussian PDF to provide a precise, real-time probability forecast for the NPC's behavioral state."
      ]
    },

    "author_notes": "This framework establishes a fundamental law of psychometrics for the DxD world. It is not a game mechanic bolted on, but an in-universe physical process. The Omegatrix does not create these probabilities; it merely measures and reports them. This ensures that NPC behavior is dynamic, realistic, and deeply integrated with the world's location and mana systems.",

    "prompt_context": "When generating an NPC's behavior, you must first calculate their P_mean based on their location and current events. Then, 'roll' against the probability distribution defined by their Gaussian PDF (with variance determined by their Soul Coherence). An NPC in a happy place (high positive E-vector) will usually be happy, but there is always a small, calculable chance they will be sad or angry, reflecting the complexity of real personality."
  }
}

