{
  "lore_entry": {
    "name": "Global OOC-GPS: Coordinate System Framework",
    "type": "Mechanics",
    "level": "Omega",
    "description": "The definitive mathematical framework for a global-scale Out-of-Character GPS system. This system uses a real-world map projection method (Equirectangular Projection) to convert the Earth's spherical coordinates (Latitude, Longitude) into a single, flat, meter-based X/Z grid, allowing for the precise triangulation and navigation of any location on the planet.",

    "core_principle": {
      "name": "Spherical to Planar Projection",
      "method": "Equirectangular Projection",
      "rationale": "Chosen for its mathematical simplicity and direct, linear relationship between angular coordinates (latitude/longitude) and planar coordinates (X/Z), making it ideal for a calculative, game-like system."
    },

    "global_origin_point": {
      "name": "Prime Vector",
      "definition": "The absolute origin of the global coordinate system, located at the intersection of the Earth's Prime Meridian and the Equator.",
      "coordinates": "(X: 0, Z: 0, Y: 0, M: 0)",
      "real_world_location": "Gulf of Guinea, Atlantic Ocean (0¬∞ N Latitude, 0¬∞ E Longitude)"
    },

    "required_constants": {
      "earth_equatorial_circumference": {
        "symbol": "C_eq",
        "value": 40075017.0,
        "unit": "meters"
      }
    },

    "conversion_formulas": {
      "x_axis_longitude_to_meters": {
        "purpose": "Calculates the East-West displacement from the Prime Meridian.",
        "formula": "X = (Longitude / 360) * C_eq",
        "variables": {
          "Longitude": "The angular distance in degrees (-180 to +180) from the Prime Meridian.",
          "C_eq": "The Earth's Equatorial Circumference in meters."
        },
        "note": "This linearly maps the 360¬∞ of longitude to the full circumference of the planet."
      },
      "z_axis_latitude_to_meters": {
        "purpose": "Calculates the North-South displacement from the Equator.",
        "formula": "Z = (Latitude / 180) * (C_eq / 2)",
        "variables": {
          "Latitude": "The angular distance in degrees (-90 to +90) from the Equator.",
          "C_eq": "The Earth's Equatorial Circumference in meters."
        },
        "note": "This linearly maps the 180¬∞ arc from the South Pole to the North Pole to half the Earth's circumference."
      }
    },

    "unmodified_axes": {
      "y_axis_altitude": "Represents the height in meters relative to mean sea level at the specific (X, Z) location. Its function is unchanged.",
      "m_axis_stratum": "Represents the dimensional plane. This entire global system is defined within the Human World stratum (M=0). Other strata would require their own coordinate systems or constants."
    },

    "worked_example_triangulation": {
      "location": "Kuoh Town (based on Kuoh, Okayama, Japan)",
      "real_world_coordinates": {
        "latitude": 34.66,
        "longitude": 133.93
      },
      "calculation_steps": {
        "x_calculation": "X = (133.93 / 360) * 40075017.0 ‚âà +14,908,426 meters",
        "z_calculation": "Z = (34.66 / 180) * (40075017.0 / 2) ‚âà +3,857,210 meters"
      },
      "final_global_coordinate": "(X: 14908426, Z: 3857210, Y: 0, M: 0)"
    },

    "author_notes": "This entry establishes the foundational physics for global navigation. All location entries in the lorebook from this point forward must use this global coordinate system for consistency. The choice of Equirectangular Projection sacrifices some visual accuracy (polar distortion) for extreme mathematical predictability and ease of calculation, which is the primary goal of this RPG system.",

    "prompt_context": "You must use this global framework to determine the coordinates of any location on Earth. The origin point is the Prime Meridian/Equator intersection, not Kuoh Town. When calculating long-distance travel, first convert the start and end points' latitude/longitude into global (X, Z) coordinates, then apply the standard distance formula. All movement must be resolved on this single, continuous world grid."
  }
}

