        function extractCommands(text) {
            const commands = [];
            const commandRegex = /(updateRow|insertRow|deleteRow|addWorldEvent|addSmallSummary|addLargeSummary|updateAttribute|equipItem|unequipItem|createLocation|updateRegion|deleteLocation)\s*\(/g;
            let match;

            while ((match = commandRegex.exec(text)) !== null) {
                const commandName = match[1];
                const startIndex = match.index + commandName.length;
                let openParens = 0;
                let endIndex = -1;

                for (let i = startIndex; i < text.length; i++) {
                    if (text[i] === '(') {
                        openParens++;
                    } else if (text[i] === ')') {
                        openParens--;
                        if (openParens === 0) {
                            endIndex = i;
                            break;
                        }
                    }
                }

                if (endIndex !== -1) {
                    const fullCommand = text.substring(match.index, endIndex + 1);
                    commands.push(fullCommand);
                    commandRegex.lastIndex = endIndex;
                }
            }
            return commands;
        }

        function parseTableEditCommands(commandsString) {
            const delta = [];
            let smallSummary = null;
            let largeSummary = null;

            const commands = extractCommands(commandsString);

            commands.forEach(commandStr => {
                const match = commandStr.match(/^(\w+)\(([\s\S]*)\)$/);
                if (!match) return;

                const command = match[1];
                const argsString = match[2];

                try {
                    let id, data, attribute, value, slot, item;
                    switch(command) {
                        case 'addSmallSummary':
                            smallSummary = JSON.parse(argsString);
                            break;
                        case 'addLargeSummary':
                            largeSummary = JSON.parse(argsString);
                            break;
                        case 'addWorldEvent':
                            const eventArgs = JSON.parse(`[${argsString}]`);
                            delta.push({ command: 'addWorldEvent', eventData: { "0": eventArgs[0], "1": eventArgs[1], "2": eventArgs[2], "id": `W${crypto.randomUUID()}` } });
                            showDanmaku('‰∏ñÁïåÂ§ß‰∫ãËÆ∞Êõ¥Êñ∞', 'world');
                            break;
                        case 'createLocation':
                            const locArgs = JSON.parse(argsString);
                            delta.push({ command: 'createLocation', locationData: locArgs });
                            break;
                        case 'updateRegion':
                            const regionArgs = JSON.parse(argsString);
                            delta.push({ command: 'updateRegion', regionData: regionArgs });
                            break;
                        case 'deleteLocation':
                            const locId = JSON.parse(argsString);
                            delta.push({ command: 'deleteLocation', locationId: locId });
                            break;
                        case 'insertRow':
                            data = JSON.parse(argsString);
                            delta.push({ command, data });
                            id = data['0'];
                            if(id.startsWith('C')) {
                                updatedCharacterIds.add(id);
                                showDanmaku(`Êñ∞‰∫∫Áâ© ${data['1'].split('|')[0]} ÁôªÂú∫`, 'npc');
                            } else if (id.startsWith('I')) {
                                showDanmaku(`Ëé∑ÂæóÊñ∞Áâ©ÂìÅ: ${data['1']}`, 'item');
                            } else {
                                showDanmaku('‰∏ñÁïåÂèëÁîü‰∫Ü‰∏Ä‰∫õÂèòÂåñ', 'world');
                            }
                            break;
                        case 'updateAttribute':
                            const attrArgs = JSON.parse(`[${argsString}]`);
                            id = attrArgs[0];
                            attribute = attrArgs[1];
                            value = attrArgs[2];
                            delta.push({ command, id, attribute, value });
                            if(id.startsWith('C') || id.startsWith('G')) updatedCharacterIds.add(id);
                            break;
                        case 'updateRow':
                            const firstCommaIndex = argsString.indexOf(',');
                            id = JSON.parse(argsString.substring(0, firstCommaIndex));
                            const dataString = argsString.substring(firstCommaIndex + 1).trim();
                            data = JSON.parse(dataString);
                            delta.push({ command, id, data });
                            if(id.startsWith('C') || id.startsWith('G')) {
                                updatedCharacterIds.add(id);
                                const char = characterDatabase[id];
                                const name = char ? char.name : 'Êüê‰∫∫';
                                showDanmaku(`${name} Áä∂ÊÄÅÊîπÂèò`, 'npc');
                            } else if (id === 'B1') {
                                showDanmaku('Áé©ÂÆ∂Áä∂ÊÄÅÊîπÂèò', 'player');
                            } else {
                                showDanmaku('‰∏ñÁïåÂèëÁîü‰∫Ü‰∏Ä‰∫õÂèòÂåñ', 'world');
                            }
                            break;
                        case 'deleteRow':
                            id = JSON.parse(argsString);
                            delta.push({ command, id });
                            if(id.startsWith('C')) updatedCharacterIds.add(id);
                            if(id.startsWith('I')) {
                                const item = inventoryItems.find(i => i.id === id);
                                showDanmaku(`Â§±ÂéªÁâ©ÂìÅ: ${item ? item.name : ''}`, 'item');
                            } else {
                                showDanmaku('‰∏ñÁïåÂèëÁîü‰∫Ü‰∏Ä‰∫õÂèòÂåñ', 'world');
                            }
                            break;
                        case 'equipItem':
                        case 'unequipItem':
                            const equipArgs = JSON.parse(`[${argsString}]`);
                            id = equipArgs[0];
                            slot = equipArgs[1];
                            item = equipArgs[2];
                            delta.push({ command, id, slot, item });
                            break;
                    }
                } catch (e) {
                    console.error(`Ëß£ÊûêÊåá‰ª§Êó∂Âá∫Èîô: ${commandStr}`, e);
                }
            });

            if (updatedCharacterIds.size > 0) {
                const btn = document.getElementById('surrounding-characters-button');
                if (!btn.querySelector('.red-dot')) {
                    const dot = document.createElement('span');
                    dot.className = 'red-dot';
                    btn.appendChild(dot);
                }
            }
            return { delta, smallSummary, largeSummary };
        }

