        function parseAIResponse(rawResponse) {
            // 1. Process with chain regex first
            let processedResponse = processTextWithRules(rawResponse, PLACEMENT_MAP.AI_OUTPUT, 'chain');

            // 2. Extract table commands
            const tableEditMatch = processedResponse.match(/<tableEdit>([\s\S]*?)<\/tableEdit>/);
            const tableEditContent = tableEditMatch ? tableEditMatch[1].replace(/<!--|-->/g, '').trim() : '';
            let contentWithoutTable = processedResponse.replace(/<tableEdit>[\s\S]*?<\/tableEdit>/g, '');

            // 3. Extract branch options
            const branchMatch = contentWithoutTable.match(/<branches>([\s\S]*?)<\/branches>/);
            let branchOptions = [];
            let branchContent = '';
            if (branchMatch) {
                branchContent = branchMatch[0];
                const optionRegex = /\[(.*?)\]/g;
                let option;
                while ((option = optionRegex.exec(branchMatch[1])) !== null) {
                    branchOptions.push(option[1]);
                }
            }
            contentWithoutTable = contentWithoutTable.replace(/<branches>[\s\S]*?<\/branches>/g, '');

            // 4. Extract and trigger image generation
            const picGenRegex = /<pic[^>]*\sprompt="([^"]*)"[^>]*?>/g;
            if (chatBackgroundSettings.autoImageGen) {
                let picMatch;
                while ((picMatch = picGenRegex.exec(contentWithoutTable)) !== null) {
                    const prompt = picMatch[1];
                    if (prompt) {
                        handleAIGenRequest(prompt, true); // true for auto-mode
                    }
                }
            }
            contentWithoutTable = contentWithoutTable.replace(picGenRegex, '');

            // 5. Process the remaining content with regular regex
            let finalContent = processTextWithRules(contentWithoutTable, PLACEMENT_MAP.AI_OUTPUT, 'regular');

            return {
                tableEditCommands: tableEditContent,
                branchOptions: branchOptions,
                branchContent: branchContent,
                content: finalContent.trim(),
                rawResponse: rawResponse 
            };
        }

        function parseRegexString(regexString) {
            const lastSlash = regexString.lastIndexOf('/');
            if (regexString.startsWith('/') && lastSlash > 0) {
                const pattern = regexString.substring(1, lastSlash);
                const flags = regexString.substring(lastSlash + 1);
                return { pattern, flags };
            }
            return { pattern: regexString, flags: 'g' };
        }

        function processTextWithRules(text, scope, type) {
            let processedText = text;
            const rules = type === 'chain' ? (regexConfig.chainRules || []) : (regexConfig.rules || []);

            rules.forEach(rule => {
                if (!rule.disabled && rule.placement && rule.placement.includes(scope)) {
                    try {
                        const { pattern, flags } = parseRegexString(rule.findRegex);
                        const regex = new RegExp(pattern, flags);
                        processedText = processedText.replace(regex, rule.replaceString);
                    } catch (e) {
                        console.error(`Êó†ÊïàÁöÑ${type}Ê≠£ÂàôË°®ËææÂºè: ${rule.findRegex}`, e);
                    }
                }
            });

            return processedText;
        }

