            affixList.innerHTML = '';
            if (enhancementManagerState.mainItem) {
                const affixes = (enhancementManagerState.mainItem.effect || '').split(',').map(s => s.trim()).filter(Boolean);
                affixes.forEach((affix, index) => {
                    const item = document.createElement('div');
