        async function callParentApiForSummary(textToSummarize) {
            if (window.parent && window.parent.TavernHelper && typeof window.parent.TavernHelper.generate === 'function') {
                const tavernGenerateFunc = window.parent.TavernHelper.generate;
                const prompt = `${summaryConfig.prompt}\n\n‰ª•‰∏ãÊòØÈúÄË¶ÅÊÄªÁªìÁöÑÂÜÖÂÆπÔºö\n${textToSummarize}`;
                const params = { user_input: prompt, should_stream: false, disable_extras: true };
                try {
                    const finalAiResponse = await tavernGenerateFunc(params);
                    return finalAiResponse.trim();
                } catch (e) {
                    console.error("TavernHelper.generate call for summary error:", e);
                    throw new Error('Áà∂Á™óÂè£AIÁîüÊàêÂ§±Ë¥•„ÄÇ');
                }
            } else {
                throw new Error('Áà∂Á™óÂè£AI(TavernHelper)Êú™ÊâæÂà∞„ÄÇ');
            }
        }

        async function callSummaryApi(textToSummarize) {
            const thinkingMessage = addMessageToLog({ content: 'Ê≠£Âú®ËØ∑Ê±ÇÂ§©Êú∫ËøõË°åÊÄªÁªì...' }, 'system');
            try {
                let summaryResult;
                if (summaryConfig.apiSource === 'parent') {
                    summaryResult = await callParentApiForSummary(textToSummarize);
                } else {
                    const { apiUrl, apiKey, apiModel, prompt } = summaryConfig;
                    if (!apiUrl || !apiModel) {
                        throw new Error('Ëá™ÂÆö‰πâAPIÈÖçÁΩÆ‰∏çÂÆåÊï¥ÔºåÊó†Ê≥ïÊÄªÁªì„ÄÇ');
                    }
                    const response = await fetch(apiUrl.endsWith('/chat/completions') ? apiUrl : `${apiUrl}/chat/completions`, {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${apiKey}` },
                        body: JSON.stringify({
                            model: apiModel,
                            messages: [{ role: 'system', content: prompt }, { role: 'user', content: textToSummarize }]
                        })
                    });
                    if (!response.ok) {
                        const errorData = await response.json();
                        throw new Error(`API ËØ∑Ê±ÇÂ§±Ë¥• (${response.status}): ${errorData.error.message}`);
                    }
                    const data = await response.json();
                    summaryResult = data.choices[0].message.content;
                }
                if (thinkingMessage && thinkingMessage.parentNode) {
                    mainContentArea.removeChild(thinkingMessage);
                }
                return summaryResult;
            } catch (error) {
                if (thinkingMessage && thinkingMessage.parentNode) {
                    mainContentArea.removeChild(thinkingMessage);
                }
                await showCustomAlert(`ÊÄªÁªìÂ§±Ë¥•: ${error.message}`);
                console.error('ÊÄªÁªìAPIË∞ÉÁî®Â§±Ë¥•:', error);
                return null;
            }
        }

        function findNextSummaryStartIndex(logs) {
            let lastSummaryIndex = -1;
            for (let i = 0; i < logs.length; i++) {
                if (logs[i].content.startsWith('[Â§©ÈÅìÊÄªÁªì:')) {
                    lastSummaryIndex = i;
                }
            }
            return lastSummaryIndex + 1;
        }

        async function runAutoSummaryCheck() {
            if (!summaryConfig.autoSummary || !currentArchiveName) return;

            const archive = await db.archives.get(currentArchiveName);
            const logs = archive.data.logs || [];
            const startIndex = findNextSummaryStartIndex(logs);
            const unsummarizedCount = logs.length - startIndex;

            if (unsummarizedCount >= summaryConfig.autoThreshold) {
                addMessageToLog({ content: `Ê£ÄÊµãÂà∞ ${unsummarizedCount} Êù°Êú™ÊÄªÁªìÊ∂àÊÅØÔºåÂ∑≤ËææÂà∞ÈòàÂÄº ${summaryConfig.autoThreshold}ÔºåÂºÄÂßãËá™Âä®ÊÄªÁªì...` }, 'system');
                await executeSummary(currentArchiveName, summaryConfig.autoThreshold);
            }
        }

