        async function handleResummarize(logId) {
            if (!await showCustomConfirm("Á°ÆÂÆöË¶ÅÈáçÊñ∞ÊÄªÁªìËøôÊÆµËÆ∞ÂΩïÂêóÔºüÊóßÁöÑÊÄªÁªìÂ∞ÜË¢´Ë¶ÜÁõñ„ÄÇ")) return;

            let archive = await db.archives.get(currentArchiveName);
            let logs = archive.data.logs;
            const summaryLogIndex = logs.findIndex(log => log.id === logId);
            if (summaryLogIndex === -1) {
                await showCustomAlert("ÈîôËØØÔºöÊâæ‰∏çÂà∞Ë¶ÅÈáçÊñ∞ÊÄªÁªìÁöÑËÆ∞ÂΩï„ÄÇ");
                return;
            }

            const summaryLog = logs[summaryLogIndex];
            const rangeMatch = summaryLog.content.match(/\[Â§©ÈÅìÊÄªÁªì: (\d+)-(\d+)Â±Ç\]/);
            if (!rangeMatch) {
                await showCustomAlert("ÈîôËØØÔºöÊó†Ê≥ï‰ªéÊ≠§ÊÄªÁªì‰∏≠Ëß£ÊûêÂá∫ÊúâÊïàÁöÑÊ•ºÂ±ÇËåÉÂõ¥„ÄÇ");
                return;
            }

            const start = parseInt(rangeMatch[1]);
            const end = parseInt(rangeMatch[2]);

            const chatLogs = logs.filter(log => !log.content.startsWith('[Â§©ÈÅìÊÄªÁªì:') && !log.content.includes('<h4>Â§©ÈÅìÂàùÂêØ</h4>'));
            const logsToSummarize = chatLogs.slice(start - 1, end);

            if (logsToSummarize.length === 0) {
                await showCustomAlert("ÈîôËØØÔºöÊâæ‰∏çÂà∞ÂéüÂßãËÆ∞ÂΩïËøõË°åÈáçÊñ∞ÊÄªÁªì„ÄÇ");
                return;
            }

            const textToSummarize = logsToSummarize.map(log => log.content).join('\n---\n');
            const summaryResult = await callSummaryApi(textToSummarize);

            if (summaryResult) {
                summaryLog.content = `[Â§©ÈÅìÊÄªÁªì: ${start}-${end}Â±Ç]\n${summaryResult}`;
                archive.data.logs = logs;
                await db.archives.put(archive);

                await showCustomAlert("ÈáçÊñ∞ÊÄªÁªìÂÆåÊàêÔºÅ");
                showSummaryDetail(logId);
                loadChatHistory(logs);
            }
        }

