                <div class="creation-grid">
                    ${Object.keys(CREATION_CONFIG.BIRTHS).map(name => {
                        const data = CREATION_CONFIG.BIRTHS[name];
                        let isSelected = false;
                        let cardContent;

                        if (name === 'Ëá™ÂÆö‰πâ') {
                            isSelected = creationState.selectedBirth === 'Ëá™ÂÆö‰πâ';
                            cardContent = `
                                <h4>Ëá™ÂÆö‰πâ <span class="cost">(${data.cost}ÁÇπ)</span></h4>
                                <p>${creationState.customBirth ? `Â∑≤ÈÄâ: ${creationState.customBirth.tag}` : 'ÁÇπÂáªÁÆ°ÁêÜËá™ÂÆö‰πâÂá∫Ë∫´'}</p>
                            `;
                        } else {
                            isSelected = creationState.selectedBirth === name;
                            cardContent = `
                                <h4>${name} <span class="cost">(${data.cost}ÁÇπ)</span></h4>
                                <p>${data.desc}</p>
                                ${Object.entries(data.bonus).map(([attr, val]) => `<p class="${val > 0 ? 'bonus' : 'penalty'}">${val > 0 ? '+' : ''} ${attr} ${val}</p>`).join('')}
