        function createLinggenOrb(name, isSelectable = false) {
            if (!name) return document.createElement('div');

            // 1. Ëß£ÊûêÁÅµÊ†πÂêçÁß∞Êù•Ëé∑ÂèñÁ±ªÂûãÂíåÂÖÉÁ¥†
            const parts = name.split(' ');
