            const elements = parts.slice(1).map(el => el.replace(',', '')); // e.g., ["Êú®", "Èõ∑"]

            // Â¶ÇÊûúÊó†Ê≥ïËß£ÊûêÂá∫Á±ªÂûãÔºåÂàôËøîÂõûÁ©∫div
            if (!type) return document.createElement('div');

            // 2. Â∞ùËØï‰ªéÈÖçÁΩÆ‰∏≠Ëé∑ÂèñÈ¢ÑËÆæÊï∞ÊçÆÔºà‰∏ªË¶Å‰∏∫‰∫ÜËé∑ÂèñËá™ÈÄâÊó∂ÁöÑcostÔºâ
            const data = CREATION_CONFIG.LINGGEN[name];

            const orb = document.createElement('div');
            // 3. Ê†∑ÂºèÂè™‰æùËµñ‰∫éËß£ÊûêÂá∫ÁöÑ`type`
            orb.className = `linggen-orb orb-type-${type}`;
            orb.innerHTML = `
                <div class="linggen-tag tag-type-${type}">${type}</div>
                <div class="linggen-elements">${elements.join(' ')}</div>
                ${(isSelectable && data) ? `<div class="linggen-cost">${data.cost}ÁÇπ</div>` : ''}
            `;

            // 4. Â¶ÇÊûúÊòØÁî®‰∫éËá™ÈÄâÈù¢ÊùøÔºåÂàôÊ∑ªÂä†ÁÇπÂáª‰∫ã‰ª∂
            if (isSelectable && data) {
                orb.onclick = async () => {
                    const oldCost = creationState.selectedLinggen ? (CREATION_CONFIG.LINGGEN[creationState.selectedLinggen]?.cost || 0) : 0;
                    if (calculateRemainingPoints() + oldCost < data.cost) {
                        await showCustomAlert('ÁÇπÊï∞‰∏çË∂≥ÔºåÊó†Ê≥ïÈÄâÊã©Ê≠§ÁÅµÊ†πÔºÅ');
