        async function handleRandomizeLinggen() {
            const cost = 2;
            const oldCost = creationState.selectedLinggen ? (CREATION_CONFIG.LINGGEN[creationState.selectedLinggen]?.cost || 0) : 0;
            if (calculateRemainingPoints() + oldCost < cost) {
                await showCustomAlert('Â±ûÊÄßÁÇπ‰∏çË∂≥ÔºåÊó†Ê≥ïÊµãÂÆöÁÅµÊ†πÔºÅ');
                return;
            }

            // ÂÖàÊâ£Èô§ÊóßÁÅµÊ†πÁöÑÁÇπÊï∞ÔºåÂÜçÂä†‰∏äÊñ∞ÁÅµÊ†πÁöÑÁÇπÊï∞
            creationState.totalPoints -= cost;

            const attributes = ["Èáë", "Êú®", "Ê∞¥", "ÁÅ´", "Âúü", "ÂÜ∞", "È£é", "Èõ∑"];
            const roll = Math.random() * 100;
            let numAttributes;
            let type;

            if (roll < 2) { // 2% chance for Tian Linggen
                numAttributes = 1;
                type = "Â§©ÁÅµÊ†π";
            } else if (roll < 22) { // 20% chance for Zhen Linggen (2-3)
                numAttributes = Math.random() < 0.7 ? 2 : 3; // 70% for 2, 30% for 3
                type = "ÁúüÁÅµÊ†π";
            } else if (roll < 23) { // 1% for no linggen
                creationState.selectedLinggen = 'Êó†ÁÅµÊ†π';
                renderCreationStep();
                return;
            } else { // 77% chance for Wei Linggen (4-5)
                numAttributes = Math.random() < 0.8 ? 4 : 5; // 80% for 4, 20% for 5
                type = "‰º™ÁÅµÊ†π";
            }

            const shuffled = attributes.sort(() => 0.5 - Math.random());
            const selectedAttributes = shuffled.slice(0, numAttributes);

            creationState.selectedLinggen = `${type} ${selectedAttributes.join(', ')}`;
            renderCreationStep();
        }

