            if (typeof eventEmit !== 'function' || typeof eventOn !== 'function') {
                if (!isAuto) await showCustomAlert('ÈîôËØØÔºöÊú™ÊâæÂà∞Ê≤πÁå¥Êèí‰ª∂ÈÄö‰ø°Êé•Âè£ (eventEmit/eventOn)„ÄÇ');
                return;
            }

            const width = parseInt(document.getElementById('ai-gen-width').value);
            const height = parseInt(document.getElementById('ai-gen-height').value);
            const statusEl = document.getElementById('ai-gen-status');

            if (!prompt) {
                if (!isAuto) statusEl.textContent = 'ÊèêÁ§∫ËØç‰∏çËÉΩ‰∏∫Á©∫ÔºÅ';
                return;
            }

            const requestId = `bg_${Date.now()}`;
            const requestData = { id: requestId, prompt, width, height, type: 1 };

            if (!isAuto) statusEl.innerHTML = '<i class="fas fa-spinner fa-spin"></i> Ê≠£Âú®ÂèëÈÄÅËØ∑Ê±Ç...';
            else showDanmaku('Ê£ÄÊµãÂà∞ÂõæÁâáÊåá‰ª§ÔºåÂºÄÂßãËá™Âä®ÁîüÂõæ...', 'world');

            eventEmit('generate_image_request', requestData);

            const responseListener = (responseData) => {
                if (responseData.id === requestId) {
                    if (responseData.success && responseData.imageData) {
                        if (!isAuto) statusEl.textContent = 'ÁîüÊàêÊàêÂäüÔºÅÊ≠£Âú®‰øùÂ≠ò...';
                        db.backgrounds.add({ data: responseData.imageData }).then(newId => {
                            chatBackgroundSettings.activeBackgroundId = newId;
                            applyChatBackground();
                            renderBackgroundThumbnails();
                            if (!isAuto) {
                                statusEl.textContent = 'ËÉåÊôØÂ∑≤ËÆæÁΩÆÔºÅ';
                                setTimeout(() => aiImageGenOverlay.classList.remove('visible'), 1000);
                            } else {
                                showDanmaku('Ëá™Âä®ÁîüÂõæÂÆåÊàêÔºåËÉåÊôØÂ∑≤Êõ¥Êñ∞ÔºÅ', 'world');
                            }
                        }).catch(err => {
                            if (!isAuto) statusEl.textContent = `‰øùÂ≠òÂ§±Ë¥•: ${err.message}`;
                        });
                    } else {
                        if (!isAuto) statusEl.textContent = `ÁîüÊàêÂ§±Ë¥•: ${responseData.error || 'Êú™Áü•ÈîôËØØ'}`;
                        else showDanmaku(`Ëá™Âä®ÁîüÂõæÂ§±Ë¥•: ${responseData.error || 'Êú™Áü•ÈîôËØØ'}`, 'error');
                    }
                    // Clean up listener
                    if (window.removeEventOn) removeEventOn('generate_image_response', responseListener);
                }
            };
            eventOn('generate_image_response', responseListener);
        }

