        async function saveCustomAchievement() {
            const name = document.getElementById('ca-editor-name').value.trim();
            if (!name) { await showCustomAlert('ÊàêÂ∞±ÂêçÁß∞‰∏çËÉΩ‰∏∫Á©∫ÔºÅ'); return; }

            const reqType = document.getElementById('ca-editor-req-type').value;
            const reqDetails = document.getElementById('ca-editor-req-details').children;
            let requirement = { type: reqType };
            switch(reqType) {
                case 'player_attr':
                    requirement.key = reqDetails[0].value;
                    requirement.value = reqDetails[1].value;
                    break;
                case 'ai_response':
                    requirement.keyword = reqDetails[0].value;
                    break;
                case 'variable':
                    requirement.table = reqDetails[0].value;
                    requirement.id = reqDetails[1].value;
                    requirement.col = reqDetails[2].value;
                    requirement.value = reqDetails[3].value;
                    break;
            }

            const rewardType = document.getElementById('ca-editor-reward-type').value;
            const rewardDetails = document.getElementById('ca-editor-reward-details').children;
            let reward = { type: rewardType };
            let rewardCommand = '';

            if (rewardType !== 'none') {
                try {
                    switch(rewardType) {
                        case 'item':
                            reward = { ...reward, id: rewardDetails[0].value, name: rewardDetails[1].value, desc: rewardDetails[2].value, itemType: rewardDetails[3].value, effect: rewardDetails[4].value, quantity: rewardDetails[5].value };
                            rewardCommand = `insertRow(${JSON.stringify({"0":reward.id, "1":reward.name, "2":reward.itemType, "3":reward.desc, "4":reward.effect, "5":reward.quantity})})`;
                            break;
                        case 'character':
                            reward = { ...reward, id: rewardDetails[0].value, nameGender: rewardDetails[1].value, realmIdentity: rewardDetails[2].value };
                            rewardCommand = `insertRow(${JSON.stringify({"0":reward.id, "1":reward.nameGender, "2":reward.realmIdentity})})`;
                            break;
                        case 'skill':
                            reward = { ...reward, id: rewardDetails[0].value, name: rewardDetails[1].value, level: rewardDetails[2].value };
                            rewardCommand = `insertRow(${JSON.stringify({"0":reward.id, "1":reward.name, "2":reward.level})})`;
                            break;
                        case 'beast':
                             reward = { ...reward, id: rewardDetails[0].value, name: rewardDetails[1].value, level: rewardDetails[2].value };
                            rewardCommand = `insertRow(${JSON.stringify({"0":reward.id, "1":reward.name, "2":reward.level})})`;
                            break;
                        case 'task':
                            reward = { ...reward, id: rewardDetails[0].value, title: rewardDetails[1].value, desc: rewardDetails[2].value, reward: rewardDetails[3].value, punishment: rewardDetails[4].value, difficulty: rewardDetails[5].value };
                            rewardCommand = `insertRow(${JSON.stringify({"0":reward.id, "1":reward.title, "2":reward.desc, "3":reward.reward, "4":reward.punishment, "5":reward.difficulty})})`;
                            break;
                        case 'world_event':
                            reward = { ...reward, time: rewardDetails[0].value, location: rewardDetails[1].value, desc: rewardDetails[2].value };
                            rewardCommand = `addWorldEvent("${reward.time}", "${reward.location}", "${reward.desc}")`;
                            break;
                    }
                } catch(e) {
                    await showCustomAlert('Â•ñÂä±‰ø°ÊÅØÊ†ºÂºèÈîôËØØÔºåËØ∑Ê£ÄÊü•„ÄÇ');
                    return;
                }
            }

            const achievementData = {
                id: currentEditingAchievementId || crypto.randomUUID(),
                name: name,
                description: document.getElementById('ca-editor-desc').value.trim(),
                completionText: document.getElementById('ca-editor-completion-text').value.trim(),
                quality: document.getElementById('ca-editor-quality').value,
                requirement: requirement,
                reward: rewardType !== 'none' ? { ...reward, command: rewardCommand } : null
            };

            const archive = await db.archives.get(currentArchiveName);
            const customAchievements = archive.data.state.achievements.custom || [];
            if (currentEditingAchievementId) {
                const index = customAchievements.findIndex(a => a.id === currentEditingAchievementId);
                customAchievements[index] = achievementData;
            } else {
                customAchievements.push(achievementData);
            }
            archive.data.state.achievements.custom = customAchievements;
            await db.archives.put(archive);

            customAchievementEditorOverlay.classList.remove('visible');
            await openCustomAchievementManager();
        }

        async function checkAchievements(aiResponse) {
            const archive = await db.archives.get(currentArchiveName);
            if (!archive || !archive.data.state.achievements) return;

            const { completed, custom } = archive.data.state.achievements;
            const uncompleted = custom.filter(ach => !completed.includes(ach.id));

            let changed = false;
            for (const ach of uncompleted) {
                let isCompleted = false;
                const req = ach.requirement;
                switch (req.type) {
                    case 'player_attr':
                        const attrValue = currentPlayerData.detailedAttributes[req.key]?.current || 0;
                        if (attrValue >= parseInt(req.value)) isCompleted = true;
                        break;
                    case 'ai_response':
                        if (aiResponse.includes(req.keyword)) isCompleted = true;
                        break;
                    case 'variable':
                        try {
                            const value = currentState[req.table]?.[req.id]?.[req.col];
                            if (typeof value === 'string' && value.includes(req.value)) isCompleted = true;
                        } catch (e) { /* Ignore errors from invalid paths */ }
                        break;
                }

                if (isCompleted) {
                    completed.push(ach.id);
                    changed = true;
                    const danmakuTypeMap = { 
                        'Âπ≥Â∫∏': 'npc', 'ÊôÆÈÄö': 'player', 'Âè≤ËØó': 'item', 
                        '‰º†ËØ¥': 'world', 'Á•ûËøπ': 'error' 
                    };
                    const danmakuType = danmakuTypeMap[ach.quality] || 'world';
                    showDanmaku(`ÊàêÂ∞±ËææÊàê: ${ach.completionText || ach.name}`, danmakuType);

                    if (ach.reward && ach.reward.command) {
                        showDanmaku(`ÊàêÂ∞±Â•ñÂä±: ${ach.reward.description || 'Â∑≤ÂèëÊîæ'}`, 'item');
                        const { delta } = parseTableEditCommands(ach.reward.command);
                        currentState = applyChanges(currentState, delta);
                        syncStateFromTables();

                        // Âº∫Âà∂Âà∑Êñ∞ÊâÄÊúâÂèØËÉΩÂèóÂΩ±ÂìçÁöÑUI
                        renderInventory(inventoryItems);
                        renderPlayerAttributes(currentPlayerData);
                        // Â¶ÇÊûúÊúâÂÖ∂‰ªñÂèØËÉΩË¢´ÊàêÂ∞±ÂΩ±ÂìçÁöÑUIÔºå‰πüÂú®ËøôÈáåË∞ÉÁî®ÂÖ∂Ê∏≤ÊüìÂáΩÊï∞
                        // ‰æãÂ¶ÇÔºåÂ¶ÇÊûúÊàêÂ∞±ÂèØËÉΩÊ∑ªÂä†ÊäÄËÉΩÊàñÁÅµÂÖΩÔºö
                        // openSkillsOverlay(); // Âà∑Êñ∞ÊäÄËÉΩËßÜÂõæÔºàÂ¶ÇÊûúÂ∑≤ÊâìÂºÄÔºâ
                        // showSpiritBeastListView(); // Âà∑Êñ∞ÁÅµÂÖΩÂàóË°®ÔºàÂ¶ÇÊûúÂ∑≤ÊâìÂºÄÔºâ
                    }
                }
            }

            if (changed) {
                archive.data.state.achievements.completed = completed;
                await db.archives.put(archive);
            }
        }

        async function openDinoGame() {
            if (activeDinoGame) {
