                        <button class="major-action-button remove-rule-btn" style="width:auto; padding: 5px 10px;"><i class="fas fa-trash"></i></button>
                    </div>
                    <div class="alignment-attr-grid">
                        ${Object.keys(CREATION_CONFIG.ATTRIBUTES).map(attr => `
                            <div class="alignment-attr-item">
                                <label for="align-${rule.id}-${attr}">${attr}</label>
                                <input type="number" id="align-${rule.id}-${attr}-max" data-attr="${attr}" data-type="max" value="${rule.attributes[attr]?.max || 0}" placeholder="‰∏äÈôê">
                                <input type="number" id="align-${rule.id}-${attr}-float" data-attr="${attr}" data-type="float" value="${rule.attributes[attr]?.float || 0}" placeholder="ÊµÆÂä®">
                            </div>
                        `).join('')}
                    </div>
                `;
                container.appendChild(ruleEl);
            });
            container.querySelectorAll('.remove-rule-btn').forEach(btn => {
                btn.addEventListener('click', (e) => {
                    const ruleId = e.target.closest('.alignment-rule').dataset.id;
                    attributeAlignmentConfig.rules = attributeAlignmentConfig.rules.filter(r => r.id !== ruleId);
                    renderAttributeAlignmentUI();
                });
            });
        }

        function addAlignmentRule() {
            attributeAlignmentConfig.rules.push({
                id: crypto.randomUUID(),
                realmKeyword: '',
                attributes: {}
            });
            renderAttributeAlignmentUI();
        }

        function saveAttributeAlignmentConfig() {
            attributeAlignmentConfig.enabled = document.getElementById('attribute-alignment-enabled-toggle').checked;
            const newRules = [];
            document.querySelectorAll('.alignment-rule').forEach(ruleEl => {
                const rule = {
                    id: ruleEl.dataset.id,
                    realmKeyword: ruleEl.querySelector('.realm-keyword-input').value.trim(),
                    attributes: {}
                };
                if (rule.realmKeyword) {
                    ruleEl.querySelectorAll('.alignment-attr-item').forEach(attrEl => {
                        const attrName = attrEl.querySelector('label').textContent;
                        const max = parseInt(attrEl.querySelector('[data-type="max"]').value) || 0;
                        const float = parseInt(attrEl.querySelector('[data-type="float"]').value) || 0;
                        if (max > 0) {
                            rule.attributes[attrName] = { max, float };
                        }
                    });
                    newRules.push(rule);
                }
            });
            attributeAlignmentConfig.rules = newRules;
            dbSet(ATTRIBUTE_ALIGNMENT_KEY, attributeAlignmentConfig);
            showCustomAlert('Â±ûÊÄßÂØπÈΩêËÆæÁΩÆÂ∑≤‰øùÂ≠òÔºÅ');
            attributeAlignmentOverlay.classList.remove('visible');
        }

        async function applyAttributeAlignment() {
            if (!attributeAlignmentConfig.enabled) return;

            let changed = false;
            const allChars = Object.values(currentState['0'] || {});

            for (const charRow of allChars) {
                const charRealm = (charRow['2'] || '').split('|')[0].trim();
                const charName = (charRow['1'] || '|').split('|')[0].trim();
                if (!charRealm) continue;

                for (const rule of attributeAlignmentConfig.rules) {
                    if (charRealm.includes(rule.realmKeyword)) {
                        let attributes = {};
                        try {
                            attributes = JSON.parse(charRow['11'] || '{}');
                        } catch(e) { continue; }

                        let charChanged = false;
                        for (const attrName in rule.attributes) {
                            const ruleAttr = rule.attributes[attrName];
                            const charAttr = attributes[attrName];

                            if (charAttr && charAttr.max > ruleAttr.max) {
                                const reduction = Math.floor(Math.random() * ruleAttr.float);
                                const newMax = Math.max(ruleAttr.max, charAttr.max - reduction);
                                attributes[attrName].max = newMax;
                                attributes[attrName].current = Math.min(charAttr.current, newMax);
                                charChanged = true;
                                showDanmaku(`${charName}ÁöÑ${attrName}Â±ûÊÄßÂ∑≤ÂØπÈΩêÔºåÂΩìÂâç‰∏äÈôê‰∏∫Ôºö${newMax}`, 'world');
                            }
                        }

                        if (charChanged) {
                            charRow['11'] = JSON.stringify(attributes);
                            changed = true;
                        }
                        break; 
                    }
                }
            }

            if (changed) {
                await saveCurrentState();
                syncStateFromTables();
                renderPlayerAttributes(currentPlayerData);
                if (characterDetailOverlay.classList.contains('visible')) {
                    openCharacterDetail();
                }
            }
        }

        function sortInventory() {
            const sortedItems = [...inventoryItems].sort((a, b) => {
                if (a.type < b.type) return -1;
                if (a.type > b.type) return 1;
                return 0;
            });

            const newInventoryState = {};
            sortedItems.forEach(item => {
                newInventoryState[item.id] = currentState['1'][item.id];
            });

            currentState['1'] = newInventoryState;
            saveCurrentState();
            syncStateFromTables();
            renderInventory(inventoryItems);
            showDanmaku('ÂÇ®Áâ©Ë¢ãÂ∑≤Êï¥ÁêÜ', 'player');
        }

        function openMapSelection() {
            if (!activeMapRenderer) {
                activeMapRenderer = new MapRenderer(
                    document.getElementById('map-canvas'),
                    WORLD_MAP_DATA,
                    (locationString) => {
                        creationState.birthLocation = locationString;
                        mapSelectionOverlay.classList.remove('visible');
                        renderCreationStep();
                    },
                    false // isWorldMap = false
                );
            }
            activeMapRenderer.drawMap();
            mapSelectionOverlay.classList.add('visible');
        }

        function openWorldMap() {
            if (!activeWorldMapRenderer) {
                activeWorldMapRenderer = new MapRenderer(
                    document.getElementById('world-map-canvas'),
                    WORLD_MAP_DATA,
                    (locationString, distance) => {
                        const targetLocation = locationString.split('|')[0];
                        addAction('travel', { location: targetLocation, distance: distance });
                    },
                    true // isWorldMap = true
                );
            }
            activeWorldMapRenderer.setPlayerData(currentPlayerData);
            activeWorldMapRenderer.drawMap();
            worldMapOverlay.classList.add('visible');
        }

        class MapRenderer {
            constructor(canvas, mapData, onSelectCallback, isWorldMap = false) {
                this.canvas = canvas;
                this.ctx = canvas.getContext('2d');
                this.mapData = mapData;
                this.onSelectCallback = onSelectCallback;
                this.isWorldMap = isWorldMap;
                this.infoPanel = document.getElementById(isWorldMap ? 'world-map-info-panel' : 'map-info-panel');
                this.container = this.canvas.parentElement;
                this.scale = 1;
                this.offsetX = 0;
                this.offsetY = 0;
                this.isDragging = false;
                this.lastX = 0;
                this.lastY = 0;
                this.hoveredRegion = null;
                this.selectedPoint = null;
                this.selectedRegionInfo = null;
                this.longPressTimer = null;
                this.playerData = null;
                this.playerBlinkState = true;
                this.init();
            }

            init() {
                this.resize();
                window.addEventListener('resize', () => this.resize());
                this.canvas.addEventListener('mousedown', (e) => this.onMouseDown(e));
                this.canvas.addEventListener('mousemove', (e) => this.onMouseMove(e));
                this.canvas.addEventListener('mouseup', (e) => this.onMouseUp(e));
                this.canvas.addEventListener('mouseleave', () => { this.isDragging = false; });
                this.canvas.addEventListener('click', (e) => this.onClick(e));
                this.canvas.addEventListener('contextmenu', (e) => {
                    e.preventDefault();
                    this.clearSelection();
                });
                this.canvas.addEventListener('touchstart', (e) => {
                    e.preventDefault();
                    this.longPressTimer = setTimeout(() => {
                        this.clearSelection();
                        this.longPressTimer = null;
                    }, 500);
                    this.onMouseDown(e.touches[0]);
                }, { passive: false });
                this.canvas.addEventListener('touchmove', (e) => {
                    e.preventDefault();
                    clearTimeout(this.longPressTimer);
                    this.onMouseMove(e.touches[0]);
                }, { passive: false });
                this.canvas.addEventListener('touchend', (e) => {
                    clearTimeout(this.longPressTimer);
                    if (this.longPressTimer !== null) {
                        this.onClick(e.changedTouches[0]);
                    }
                    this.onMouseUp(e.changedTouches[0]);
                });

                if (this.isWorldMap) {
                    setInterval(() => {
                        this.playerBlinkState = !this.playerBlinkState;
                        this.drawMap();
                    }, 500);
                }
            }

            zoom(factor) {
                const newScale = Math.max(0.1, Math.min(3, this.scale * factor));

                const centerX = (this.canvas.width / 2 - this.offsetX) / this.scale;
                const centerY = (this.canvas.height / 2 - this.offsetY) / this.scale;

                this.offsetX = this.canvas.width / 2 - centerX * newScale;
                this.offsetY = this.canvas.height / 2 - centerY * newScale;

                this.scale = newScale;
                this.drawMap();
            }

            setPlayerData(playerData) {
                this.playerData = playerData;
            }

            clearSelection() {
                this.selectedPoint = null;
                this.selectedRegionInfo = null;
                this.drawMap();
                this.updateInfoPanel();
            }

            resize() {
                this.canvas.width = this.container.clientWidth;
                this.canvas.height = this.container.clientHeight;
                this.drawMap();
            }

            drawMap() {
                this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
                this.ctx.save();
                this.ctx.translate(this.offsetX, this.offsetY);
                this.ctx.scale(this.scale, this.scale);

                // Draw terrains
                this.mapData.terrains.forEach(terrain => {
                    this.ctx.fillStyle = terrain.color;
                    this.drawSmoothPath(terrain.points);
                    this.ctx.fill();
                });

                // Draw main region borders
                this.ctx.strokeStyle = '#654321';
                this.ctx.lineWidth = 3 / this.scale;
                this.mapData.main_regions.forEach(region => {
                    this.drawSmoothPath(region.points);
                    this.ctx.stroke();
                });

                // Draw sub-region borders
                this.ctx.strokeStyle = '#8B4513';
                this.ctx.lineWidth = 1.5 / this.scale;
                this.ctx.setLineDash([10 / this.scale, 5 / this.scale]);
                this.mapData.sub_regions.forEach(region => {
                    this.drawSmoothPath(region.points);
                    this.ctx.stroke();
                });
                this.ctx.setLineDash([]);

                // Draw labels
                this.ctx.fillStyle = '#4a3f35';
                this.ctx.shadowColor = 'rgba(255, 255, 255, 0.7)';
                this.ctx.shadowBlur = 5;
                this.mapData.main_regions.forEach(region => {
                    const center = this.getPolygonCenter(region.points);
                    this.ctx.font = `bold ${24 / this.scale}px 'ZCOOL XiaoWei', serif`;
                    this.ctx.textAlign = 'center';
                    this.ctx.fillText(region.name, center.x, center.y);
                });
                this.mapData.sub_regions.forEach(region => {
                    const center = this.getPolygonCenter(region.points);
                    this.ctx.font = `${16 / this.scale}px 'Noto Serif SC', serif`;
                    this.ctx.fillText(region.name, center.x, center.y);
                });
                this.ctx.shadowBlur = 0;

                // Draw points of interest
                this.mapData.points_of_interest.forEach(poi => {
                    this.ctx.beginPath();
                    this.ctx.arc(poi.x, poi.y, 5 / this.scale, 0, 2 * Math.PI);
                    this.ctx.fillStyle = '#D4AF37';
                    this.ctx.fill();
                    this.ctx.strokeStyle = '#000';
                    this.ctx.lineWidth = 1 / this.scale;
                    this.ctx.stroke();
                    this.ctx.fillStyle = '#fff';
                    this.ctx.font = `${12 / this.scale}px 'Noto Serif SC', serif`;
                    this.ctx.fillText(poi.name, poi.x, poi.y - 10 / this.scale);
                });

                // Draw selected point
                if (this.selectedPoint) {
                    this.ctx.beginPath();
                    this.ctx.arc(this.selectedPoint.x, this.selectedPoint.y, 8 / this.scale, 0, 2 * Math.PI);
                    this.ctx.fillStyle = 'rgba(255, 0, 0, 0.7)';
                    this.ctx.strokeStyle = '#fff';
                    this.ctx.lineWidth = 2 / this.scale;
                    this.ctx.fill();
                    this.ctx.stroke();
                }

                // Draw player position
                if (this.isWorldMap && this.playerData && this.playerData.fullLocationString) {
                    const locParts = this.playerData.fullLocationString.split('|');
                    if (locParts.length > 1) {
                        const coords = locParts[1].split(',').map(Number);
                        if (coords.length === 2 && !isNaN(coords[0]) && !isNaN(coords[1])) {
                            this.ctx.fillStyle = this.playerBlinkState ? 'rgba(0, 255, 0, 0.9)' : 'rgba(0, 128, 0, 0.7)';
                            this.ctx.beginPath();
                            this.ctx.arc(coords[0], coords[1], 8 / this.scale, 0, 2 * Math.PI);
                            this.ctx.fill();
                            this.ctx.strokeStyle = '#fff';
                            this.ctx.lineWidth = 2 / this.scale;
                            this.ctx.stroke();
                        }
                    }
                }

                this.ctx.restore();
                this.drawScaleRuler();
            }

            drawScaleRuler() {
                const rulerLengthPx = 100; // Ruler length in pixels
                const rulerLengthMeters = rulerLengthPx * 1000000 / this.scale; // 1px = 1,000,000 meters at scale 1
                const rulerText = rulerLengthMeters > 10000000 ? `${(rulerLengthMeters / 10000000).toFixed(1)} ‰∏áÈáå` : `${Math.round(rulerLengthMeters / 1000)} ÂÖ¨Èáå`;

                this.ctx.save();
                this.ctx.fillStyle = 'rgba(0,0,0,0.6)';
                this.ctx.fillRect(20, this.canvas.height - 40, rulerLengthPx + 20, 30);

                this.ctx.strokeStyle = '#fff';
                this.ctx.lineWidth = 2;
                this.ctx.beginPath();
                this.ctx.moveTo(30, this.canvas.height - 25);
                this.ctx.lineTo(30 + rulerLengthPx, this.canvas.height - 25);
                this.ctx.moveTo(30, this.canvas.height - 30);
                this.ctx.lineTo(30, this.canvas.height - 20);
                this.ctx.moveTo(30 + rulerLengthPx, this.canvas.height - 30);
                this.ctx.lineTo(30 + rulerLengthPx, this.canvas.height - 20);
                this.ctx.stroke();

                this.ctx.fillStyle = '#fff';
                this.ctx.font = '12px Noto Serif SC, serif';
                this.ctx.textAlign = 'center';
                this.ctx.fillText(rulerText, 30 + rulerLengthPx / 2, this.canvas.height - 28);
                this.ctx.restore();
            }

            drawSmoothPath(points) {
                if (points.length < 2) return;
                this.ctx.beginPath();
                this.ctx.moveTo(points[0][0], points[0][1]);
                for (let i = 0; i < points.length; i++) {
                    const p1 = points[i];
                    const p2 = points[(i + 1) % points.length];
                    const midPoint = { x: (p1[0] + p2[0]) / 2, y: (p1[1] + p2[1]) / 2 };
                    this.ctx.quadraticCurveTo(p1[0], p1[1], midPoint.x, midPoint.y);
                }
                this.ctx.closePath();
            }

            getPolygonCenter(points) {
                let x = 0, y = 0;
                points.forEach(p => { x += p[0]; y += p[1]; });
                return { x: x / points.length, y: y / points.length };
            }

            isPointInPolygon(point, vs) {
                let x = point.x, y = point.y;
                let inside = false;
                for (let i = 0, j = vs.length - 1; i < vs.length; j = i++) {
                    let xi = vs[i][0], yi = vs[i][1];
                    let xj = vs[j][0], yj = vs[j][1];
                    let intersect = ((yi > y) !== (yj > y)) && (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
                    if (intersect) inside = !inside;
                }
                return inside;
            }

            getMousePos(evt) {
                const rect = this.canvas.getBoundingClientRect();
                return {
                    x: (evt.clientX - rect.left - this.offsetX) / this.scale,
                    y: (evt.clientY - rect.top - this.offsetY) / this.scale
                };
            }

            onMouseDown(e) {
                this.isDragging = false; // Reset drag flag
                this.dragStartPos = { x: e.clientX, y: e.clientY };
                this.lastX = e.clientX;
                this.lastY = e.clientY;
            }

            onMouseMove(e) {
                if (this.dragStartPos) {
                    const dx = e.clientX - this.dragStartPos.x;
                    const dy = e.clientY - this.dragStartPos.y;
                    if (Math.sqrt(dx*dx + dy*dy) > 5) { // Drag threshold
                        this.isDragging = true;
                    }
                }

                if (this.isDragging) {
                    const dx = e.clientX - this.lastX;
                    const dy = e.clientY - this.lastY;
                    this.offsetX += dx;
                    this.offsetY += dy;
                    this.lastX = e.clientX;
                    this.lastY = e.clientY;
                    this.drawMap();
                } else if (!this.selectedPoint) {
                    const pos = this.getMousePos(e);
                    this.updateInfoPanel(pos);
                }
            }

            onMouseUp(e) {
                this.dragStartPos = null;
            }

            onClick(e) {
                if (this.isDragging) {
                    this.isDragging = false;
                    return;
                }
                const pos = this.getMousePos(e);
                this.selectedPoint = pos;

                let locationString = "Êú™Áü•‰πãÂú∞";
                let mainRegion = null;
                let subRegion = null;
                let poi = null;

                // Check POIs first
                const poiRadius = 10 / this.scale;
                for (const p of this.mapData.points_of_interest) {
                    const dist = Math.sqrt(Math.pow(pos.x - p.x, 2) + Math.pow(pos.y - p.y, 2));
                    if (dist < poiRadius) {
                        poi = p;
                        break;
                    }
                }

                if (poi) {
                    mainRegion = this.mapData.main_regions.find(r => r.name === poi.main_region);
                    subRegion = this.mapData.sub_regions.find(r => r.name === poi.sub_region);
                } else {
                    // Find smallest region containing the point
                    let smallestArea = Infinity;
                    let foundRegion = null;

                    const allRegions = [...this.mapData.sub_regions, ...this.mapData.main_regions];
                    allRegions.forEach(region => {
                        if (this.isPointInPolygon(pos, region.points)) {
                            const area = this.getPolygonArea(region.points);
                            if (area < smallestArea) {
                                smallestArea = area;
                                foundRegion = region;
                            }
                        }
                    });

                    if (foundRegion) {
                        if (this.mapData.sub_regions.includes(foundRegion)) {
                            subRegion = foundRegion;
                            mainRegion = this.mapData.main_regions.find(r => r.name === subRegion.main_region);
                        } else {
                            mainRegion = foundRegion;
                        }
                    }
                }

                if (mainRegion) {
                    locationString = subRegion ? `${mainRegion.name}/${subRegion.name}` : mainRegion.name;
                    if (poi) {
                        locationString += `|(${poi.name}) `;
                    }
                }

                const finalLocation = `${locationString}|${Math.round(pos.x)},${Math.round(pos.y)}`;
                this.selectedRegionInfo = {
                    locationString: locationString,
                    finalLocation: finalLocation,
                    mainRegion: mainRegion,
                    subRegion: subRegion,
                    poi: poi
                };

                this.updateInfoPanel();
                this.drawMap();
            }

            getPolygonArea(points) {
                let area = 0;
                for (let i = 0, j = points.length - 1; i < points.length; j = i++) {
                    area += (points[j][0] + points[i][0]) * (points[j][1] - points[i][1]);
                }
                return Math.abs(area / 2);
            }

            updateInfoPanel(mousePos = null) {
                if (this.selectedRegionInfo) {
                    const { locationString, finalLocation, mainRegion, subRegion, poi } = this.selectedRegionInfo;
                    let description = mainRegion ? mainRegion.description : '';
                    if (subRegion) description += `<br><br><strong>${subRegion.name}:</strong> ${subRegion.description}`;
                    if (poi) description += `<br><br><strong>${poi.name}:</strong> ${poi.description}`;

                    let distanceText = '';
                    let distance = 0;
                    if (this.isWorldMap && this.playerData && this.playerData.fullLocationString) {
                        const locParts = this.playerData.fullLocationString.split('|');
                        if (locParts.length > 1) {
                            const playerCoords = locParts[1].split(',').map(Number);
                            const targetCoords = [this.selectedPoint.x, this.selectedPoint.y];
                            const pixelDistance = Math.sqrt(Math.pow(playerCoords[0] - targetCoords[0], 2) + Math.pow(playerCoords[1] - targetCoords[1], 2));
                            distance = Math.round(pixelDistance * 1000000); // 1px = 1,000,000 meters
                            distanceText = `<br><br><strong>Ë∑ùÁ¶ª:</strong> ${distance.toLocaleString()} Á±≥`;
                        }
                    }

                    this.infoPanel.innerHTML = `<h4>Â∑≤ÈÄâÊã©: ${locationString.split('|')[0]}</h4><p>${description}${distanceText}</p><button id="confirm-location-btn" class="major-action-button">${this.isWorldMap ? 'ÂâçÂæÄÊ≠§Â§Ñ' : 'Á°ÆËÆ§Ê≠§Âú∞'}</button>`;
                    document.getElementById('confirm-location-btn').onclick = () => {
                        this.onSelectCallback(finalLocation, distance);
                    };
                } else if (mousePos) {
                    this.infoPanel.innerHTML = `<p>ÂùêÊ†á: (${Math.round(mousePos.x)}, ${Math.round(mousePos.y)})</p>`;
                } else {
                    this.infoPanel.innerHTML = `<p>Âú®Âú∞Âõæ‰∏äÁÇπÂáªÈÄâÊã©‰∏Ä‰∏™${this.isWorldMap ? 'ÁõÆÊ†á' : 'Âá∫ÁîüÁÇπ'}„ÄÇ</p>`;
                }
            }
        }

        setupEventListeners();
        initPanel();
    });
</script>

